import {Component} from 'react'
import './index.css'
import Book from '../book'

class Books extends Component{

    state = {
        searchInput : '',
        booklist:[],
    }

    searchChange = e => {
        this.setState({searchInput: e.target.value})
    }

    searchButton = async () => {
        const {searchInput} =this.state
        let details = {searchInput}
        details = JSON.stringify(details);
        //console.log(details,searchInput)
        const url = "http://localhost:3001/books"
        const options = {
            method : ['POST','GET'],
            body : details,
            headers: {
                "Content-Type": "application/json",
            }
        }
       // console.log(url,options);
        await fetch(url,options)
            .then(response => {
                return response.json();
            })
            .then(jsonData => {
                const list = jsonData;
               // this.setState({loader:false})
               this.setState({booklist:list})
               if(list.length === 0){
                   console.log("empty")
               }


            })
    }

    searchButtonProcess = () => {
        this.searchButton()
    }


    componentDidMount = () => {
        this.searchButton()
    }

    render(){
        const {booklist} = this.state

        return(
            <div className="books-container">
                <div>
                <input placeholder="search your book here" className="input-box" onChange={this.searchChange}/>
                <button className="search-button" onClick={this.searchButtonProcess}>search</button>
                </div>
                <div className="books-result">
                    <ul>
                    {booklist.map(each => (
                        <Book data={each} key={each.id}/>
                    ))}
                    </ul>
                </div>
            </div>
        )
    }
}

export default Books
