import {Component} from 'react'
import './index.css'
import Book from '../book'

class Books extends Component{

    state = {
        searchInput : '',
        booklist:[],
    }

    searchButton =async () => {
        const {searchInput} =this.state
        let details = {searchInput}
        details = JSON.stringify(details);
        //console.log(details,searchInput)
        const url = "http://localhost:3001/books"
        const options = {
            method : 'POST',
            body : details,
            headers: {
                "Content-Type": "application/json",
            }
        }
       // console.log(url,options);
        await fetch(url,options)
            .then(response => {
                return response.json();
            })
            .then(jsonData => {
                const list = jsonData;
               // this.setState({loader:false})
                this.setState({booklist:list})
                console.log(this.state.booklist)
            })
    }

    render(){
        const {booklist} = this.state

        return(
            <div className="books-container">
                <div>
                <input placeholder="search your books here" className="input-box"/>
                <button className="search-button" onClick={this.searchButton}>search</button>
                </div>
                <div className="books-result">
                    {booklist.map(each => (
                        <ul>
                            <Book data={each} key={each.id}/>
                        </ul>
                    ))}
                </div>
            </div>
        )
    }
}

export default Books
