import {Component} from 'react'

class Header extends Component{

    fun = ()=> {
        var fr=new FileReader();
            fr.onload=function(){
                console.log(fr.result)
            }

            //fr.readAsText(this.files[0]);


    }

    clickB = () =>{
        console.log("clicked")
    }
    render(){
        return(
            <div>
                <p>hello world</p>
                <input type="file" onChange={this.fun()}/>
                <button onClick={this.clickB}> click</button>
            </div>
        )
    }
}
export default Header
