import {Component} from 'react'
import './index.css'
import Book from '../book'

class Books extends Component{
    state = {
        searchInput : "",
        booklist : []
    }

    searchChange = e => {
        this.setState({searchInput : e.target.value})
    }

    render(){
        const {booklist} = this.state
        return(
            <div className="books-container">
                <div>
                <input placeholder="search your book here" className="input-box" onChange={this.searchChange}/>
                <button className="search-button" onClick={this.searchButtonProcess}>search</button>
                </div>
                <div className="books-result">
                    <ul>
                    {booklist.map(each => (
                        <Book data={each} key={each.id}/>
                    ))}
                    </ul>
                </div>
            </div>
        )
    }

}

export default  Books
